    @Test
    @DisplayName("Test for client secret Getter")
    void testForClientSecretGetter() throws NoSuchFieldException, IllegalAccessException {
        //given
        final ZenRequest zenRequest = new ZenRequest();
        final Field field = zenRequest.getClass().getDeclaredField("secret");
        field.setAccessible(true);
        field.set(zenRequest,"Test Mob");
        //when
        final String result = getSecret();
        //then
        assertEquals("Test Mob", result, "Fields didn't match");
    }

    @Test
    @DisplayName("Test for client secret Setter")
    void testForClientSecretSetter() throws NoSuchFieldException, IllegalAccessException {
        //given
        final ZenRequest zenRequest = new ZenRequest();
        setSecret("Test123");
        final Field field = zenRequest.getClass().getDeclaredField("secret");
        field.setAccessible(true);
        assertEquals("Test123", field.get(zenRequest), "Fields didn't match");
    }

